export interface AppProps {
	location: Location;
}
export interface AppState {
	is_session_valid: boolean;
}

type Properties = AppProps & FelaWithStylesProps<AppProps, AppStyles, {}>;

class App extends React.PureComponent<Properties, AppState> {
	public componentDidMount() {
		let is_scroll_active = false;
		const body_class = this.props.styles.body_no_pointer_events;
		const removePointerEvents = debounce(() => {
			removeClass(document.body, body_class);
			is_scroll_active = false;
			// tslint:disable-next-line:align
		}, 250);
		window.addEventListener('scroll', () => {
			if (!is_scroll_active) {
				is_scroll_active = true;
				addClass(document.body, body_class);
			}
			removePointerEvents();
		});
	}

	public render(): JSX.Element {
		return (
			<div className={this.props.styles.app}>
				{/*<React.StrictMode>
				</React.StrictMode>*/}
				<BackgroundVideoContainer />
				<MainApplicationRoutes location={this.props.location} />
			</div>
		);
	}
}

// tslint:disable-next-line:no-any
export default withRouter(connectWithFela<AppProps, AppStyles>(app_styles as AppStyles)(App) as any);
